import React from 'react';

const RSVPForm = () => {
  const handleSubmit = (event) => {
    const form = event.target;
    const fullName = form.fullName.value.trim();
    const email = form.email.value.trim();
    const phone = form.phone.value.trim();
    const guest = form.guest.value.trim();

    if (!fullName) {
      alert('Please enter your full name.');
      event.preventDefault();
      return;
    }
    if (!email || !/\S+@\S+\.\S+/.test(email)) {
      alert('Please enter a valid email address.');
      event.preventDefault();
      return;
    }
    if (!phone) {
      alert('Please enter your phone number.');
      event.preventDefault();
      return;
    }
    if (!guest) {
      alert('Could you confirm how many guests you anticipate bringing with you?');
      event.preventDefault();
      return;
    }

    // If all validations pass, allow Formspree to handle submission
  };

  return (
    <form
      className="w-full max-w-md bg-white p-6 rounded-lg shadow-lg"
      action="https://formspree.io/f/xdkavyok"
      method="POST"
      onSubmit={handleSubmit}
    >
      <div className="mb-4">
        <label htmlFor="fullName" className="block text-sm font-bold mb-2">
          Full Name*
        </label>
        <input
          type="text"
          id="fullName"
          name="name"
          placeholder="Type your name"
          required
          className="w-full p-3 border border-gray-300 rounded-md focus:outline-none focus:border-blue-600"
        />
      </div>

      <div className="mb-4">
        <label htmlFor="email" className="block text-sm font-bold mb-2">
          Email*
        </label>
        <input
          type="email"
          id="email"
          name="email"
          placeholder="Type your email"
          required
          className="w-full p-3 border border-gray-300 rounded-md focus:outline-none focus:border-blue-600"
        />
      </div>

      <div className="mb-4">
        <label htmlFor="phone" className="block text-sm font-bold mb-2">
          Phone*
        </label>
        <input
          type="text"
          id="phone"
          name="phone"
          placeholder="Type your phone number"
          required
          className="w-full p-3 border border-gray-300 rounded-md focus:outline-none focus:border-blue-600"
        />
      </div>

      <div className="mb-4">
        <label htmlFor="guest" className="block text-sm font-bold mb-2">
          Guest*
        </label>
        <input
          type="text"
          id="guest"
          name="guest"
          placeholder="How many guests are you bringing?"
          required
          className="w-full p-3 border border-gray-300 rounded-md focus:outline-none focus:border-blue-600"
        />
      </div>

      <div className="mb-6">
        <label htmlFor="message" className="block text-sm font-bold mb-2">
          Message*
        </label>
        <textarea
          id="message"
          name="message"
          placeholder="Tell us more about your request"
          required
          className="w-full p-3 border border-gray-300 rounded-md h-28 resize-none focus:outline-none focus:border-blue-600"
        ></textarea>
      </div>

      {/* Honeypot anti-spam field */}
      <input type="hidden" name="_gotcha" />

      <button
        type="submit"
        className="w-full bg-primary text-white py-3 rounded-md text-lg font-semibold hover:bg-secondary transition"
      >
        Submit
      </button>
    </form>
  );
};

export default RSVPForm;
