import React from 'react';
import Hero from '../components/Hero';
import EventDetails from '../components/EventDetails';
import CTASection from '../components/CTAsection';

const Home = () => {
  return (
    <main className="mt-10 font-inter">
      {/* Hero Banner */}
      <Hero />

      {/* Mission Statement */}
      <section className="py-16 px-4 text-center bg-light">
        <h2 className="text-3xl md:text-4xl font-playwrite text-gray-800 max-w-4xl mx-auto">
          Reuniting Hearts, Reliving Memories, Rekindling Friendships!
        </h2>
      </section>

      {/* Event Highlights */}
      <EventDetails />

      {/* RSVP CTA */}
      <CTASection />
    </main>
  );
};

export default Home;
