import React from 'react';

const Hero = () => {
  return (
    <section className="flex flex-col md:flex-row items-center justify-between h-[90vh] px-6 md:px-[5%] overflow-hidden bg-nav">
      {/* Text Content */}
      <div className="flex-1 z-10 text-[#333] px-4 md:px-8">
        <h1 className="text-4xl md:text-5xl font-playwrite mb-6">St Francis School</h1>
        <p className="text-lg md:text-xl font-inter leading-relaxed">
          Welcome, Class of 1997! It's time to relive the good old days, reconnect with classmates, 
          and make new memories. Don't miss this once-in-a-lifetime chance to catch up and celebrate together!
        </p>
      </div>

      {/* Image */}
      <div className="flex-1 relative mt-10 md:mt-0">
        <img
          src="/images/School_image.webp"
          alt="Reunion Event"
          className="w-full h-full object-cover object-bottom brightness-90 translate-y-[9%]"
        />
      </div>
    </section>
  );
};

export default Hero;
