import React from 'react';

const MemberCard = ({ name, role, image }) => {
  return (
    <div className="bg-gray-200 border border-gray-300 rounded-lg p-5 text-center transition-transform hover:-translate-y-1 hover:shadow-md">
      <img
        src={image}
        alt={`${name} Image`}
        className="w-[200px] h-[200px] object-cover rounded-full mx-auto mb-4 block"
      />
      <h3 className="text-lg font-playwrite mb-2 text-[#333]">{name}</h3>
      <p className="text-sm text-gray-700 m-0 font-inter">
        <strong>{role}</strong>
      </p>
    </div>
  );
};

export default MemberCard;
