import React from 'react';

const eventDetails = [
  { title: 'Date', value: 'October 24, 2025' },
  { title: 'Time', value: '3:40 PM' },
  { title: 'Venue', value: 'St. Francis High School, La Libertad, Negros Oriental' },
  { title: 'RSVP Deadline', value: 'October 1, 2025' }, // ← update as needed
];

const EventDetails = () => {
  return (
    <section className="py-16 px-4 bg-white text-center" id="event-details">
      <h3 className="text-3xl font-playwrite text-gray-800 mb-10">Event Highlights:</h3>
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-4 gap-6 max-w-6xl mx-auto">
        {eventDetails.map((detail, idx) => (
          <div
            key={idx}
            className="bg-white border border-gray-200 rounded-lg p-6 shadow hover:shadow-lg transition duration-300"
          >
            <h4 className="text-xl font-semibold text-secondary mb-2">{detail.title}</h4>
            <p className="text-gray-700">{detail.value}</p>
          </div>
        ))}
      </div>
    </section>
  );
};

export default EventDetails;
