import React from 'react';
import RSVPForm from '../components/RSVPform';

const RSVP = () => {
  return (
    <>
      {/* Page Heading */}
      <section className="text-center mt-12 mb-8">
        <h2 className="text-3xl font-playwrite text-[#333]">Registration Page</h2>
      </section>

      {/* Form Section */}
      <section className="flex flex-col lg:flex-row items-center justify-center gap-10 max-w-6xl mx-auto px-4 py-8">
        {/* Left: Image */}
        <div className="w-full lg:w-1/2">
          <img
            src="/images/School_image.webp"
            alt="St. Francis School"
            loading="lazy"
            className="w-full h-auto object-cover rounded-lg shadow-md"
          />
        </div>

        {/* Right: Form */}
        <div className="w-full lg:w-1/2 mt-8 lg:mt-0">
          <RSVPForm />
        </div>
      </section>
    </>
  );
};

export default RSVP;
